mapper.update:
    
			 update  application  set  APPLICATION_STATUS=2
   				 where
   				 application_id=#{applicationId}

    
			 update  application  set  APPLICATION_STATUS=3
   				 where
   				 application_id=#{applicationId}

mapper.select:
SELECT ST.STUDENT_ID, ST.STUDENT_NO, ST.FIRST_NAME, ST.MIDDLE_NAME, ST.LAST_NAME, ST.STATUS, a.application_status, a.application_id, a.update_time, p.position_name, e.employer_name FROM STUDENT ST , application a,position p,employer e
ST.USERNAME=a.username and p.position_id=a.position_id and e.employer_id = p.employer_id
and a.position_id = #{positionId}
