mapper.insert:
insert into education(education_id,degree,major,gpa,institution,country,graduation_date,student_id,create_time,update_time) select SEQ_EDUCATION.NEXTVAL,A.* from (
SELECT #{item.degree},#{item.major},#{item.gpa},#{item.institution},#{item.country},#{item.graduationDate},#{item.student.studentId},#{item.createTime},#{item.updateTime} FROM DUAL
) A

update.foreach:
UPDATE EDUCATION

  degree = #{degree}, major = #{major}, gpa = #{gpa}, institution = #{institution}, country = #{country}, graduation_date = #{graduation_date}

WHERE STUDENT_ID = #{studentId}
