mapper.select:
SELECT A.POSITION_ID, A.POSITION_NAME, A.APPLICATION_STATUS, e.EMPLOYER_NAME FROM (SELECT p.POSITION_ID, p.POSITION_NAME, p.EMPLOYER_ID, p.POSITION_GROUP_ID, a.APPLICATION_STATUS FROM POSITION p LEFT JOIN APPLICATION a on (p.POSITION_ID = a.POSITION_ID AND a.USERNAME = #{userName})) A, EMPLOYER e
A.EMPLOYER_ID = e.EMPLOYER_ID
AND POSITION_GROUP_ID = #{groupId}
AND A.APPLICATION_STATUS = #{positionStatus}

mapper.update:
    
			 update  position  set position_name=#{positionName},position_group_id=#{positionGroup.positionGroupId},update_time=#{updateTime},position_description=#{positionDescription},
			 responsibilities=#{responsibilities}, requirements=#{requirements}, salary=#{salary}
   				 where
   				 position_id=#{positionId}
